export function Card({ title, children, action }) {
  return (
    <div className="p-4 rounded-xl border border-gray-200 dark:border-white/10 bg-white dark:bg-neutral-900">
      {title && <h3 className="font-semibold">{title}</h3>}
      <div className="mt-2 text-sm text-gray-600 dark:text-gray-300">{children}</div>
      {action && <div className="mt-3">{action}</div>}
    </div>
  );
}

export function Badge({ children }) {
